import { ref } from "vue";
import Sidebar from "./components/layout/Sidebar.vue";
import Navbar from "./components/layout/Navbar.vue";
export default {
  name: "App",
  components: {
    Sidebar,
    Navbar
  },
  setup() {
    const isSidebarCollapsed = ref(false);
    const toggleSidebar = () => {
      isSidebarCollapsed.value = !isSidebarCollapsed.value;
    };
    return {
      isSidebarCollapsed,
      toggleSidebar
    };
  }
};
